import { Component } from '@angular/core';
import { A2sCommLink } from './a2s-comm-link';

@Component({
  moduleId: 'a2s-comm',
  selector: 'a2s-comm',
  templateUrl: './a2s-comm.component.html',
  styleUrls: ['./a2s-comm.component.css']
})
export class A2sCommComponent {
  opened: boolean = false;
  links: A2sCommLink[] = [
    {name: 'Zadania', url: 'http://hq.angular2space.pl/chapters/exercises', css: 'blue'},
    {name: 'Wiedza', url: 'http://hq.angular2space.pl/chapters/learn', css: 'blue'},
    {name: 'A2 Docs', url: 'https://angular.io/docs/ts/latest/', css: 'red'},
    {name: 'A2 API', url: 'https://angular.io/docs/ts/latest/api/', css: 'red'},
    {name: 'A2 CS', url: 'https://angular.io/docs/ts/latest/guide/cheatsheet.html', css: 'red'}
  ];

  toggle() {
    this.opened = !this.opened;
  }

  open(url: string) {
    this.toggle();
    window.open(url, '_blank');
  }
}
